mapper.select:
select a.* from( select a01.memberid ,a01.membername ,a01.email ,a01.licensegrade ,a01.celltel ,a01.businessname ,a01.businessnumber ,concat(substring(a01.businessnumber,1,3),'-',substring(a01.businessnumber,4,2),'-',substring(a01.businessnumber,6)) strbusinessnumber ,a01.joindate ,str_to_date(a01.joindate,'%Y%m%d') strjoindate ,a01.hostname ,j04.codename hostip ,a01.serverdomainname ,j01.datakeepterm ,j01.datakeepunit ,j01.basevolume + j01.servicevolume + j01.addvolume totalsoluble ,j01.basevolume ,j01.basecharge ,j01.servicevolume ,j01.addvolume ,j01.addcharge ,j02.postnumber ,j02.address ,j02.detailaddress ,concat(j02.postnumber,' ',j02.address,' ',j02.detailaddress) zipaddress ,j03.totalvolume ,j03.networkvolume ,j03.servervolume ,j03.apvolume ,j03.dbmsvolume ,j03.fmsvolume ,j03.totalcharge ,round(j03.totalvolume / (j01.basevolume + j01.servicevolume + j01.addvolume) * 100,2) userate ,row_number() over(order by a01.joindate DESC, a01.email) AS rownum from tbmember a01 inner join tbmemberlicense j01 on a01.email = j01.email left join tbtaxinformation j02 on a01.email = j02.email left join tbmemberusehistory j03 on a01.email = j03.email and(j03.email, j03.useday) in (select email, max(useday) useday from tbmemberusehistory group by email) left join tbcommoncode j04 on j04.groupcode = '100' and j04.commoncode = a01.hostname where 1 = 1 and a01.withdrawalyn != '1'
AND a01.membername like concat(#{searchName},'%')
) as a where 1 = 1
and a.rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
order by a.joindate DESC, a.email

select a.* from( select a01.memberid ,a01.membername ,a01.email ,a01.licensegrade ,a01.celltel ,a01.businessname ,a01.businessnumber ,concat(substring(a01.businessnumber,1,3),'-',substring(a01.businessnumber,4,2),'-',substring(a01.businessnumber,6)) strbusinessnumber ,a01.joindate ,str_to_date(a01.joindate,'%Y%m%d') strjoindate ,a01.hostname ,j04.codename hostip ,ifnull(a01.serverdomainname,'') serverdomainname ,j01.datakeepterm ,j01.datakeepunit ,j01.basevolume + j01.servicevolume + j01.addvolume totalsoluble ,j01.basevolume ,j01.basecharge ,j01.servicevolume ,j01.addvolume ,j01.addcharge ,j01.stdate ,j01.eddate ,str_to_date(j01.stdate,'%Y%m%d') strstdate ,str_to_date(j01.eddate,'%Y%m%d') streddate ,j02.postnumber ,j02.address ,j02.detailaddress ,concat(j02.postnumber,' ',j02.address,' ',j02.detailaddress) zipaddress ,j03.totalvolume ,j03.networkvolume ,j03.servervolume ,j03.apvolume ,j03.dbmsvolume ,j03.fmsvolume ,j03.totalcharge ,case when a01.withdrawalyn = '0' then '회원' when a01.withdrawalyn = '1' then '탈퇴' else '중지' end statusnm ,round(j03.totalvolume / (j01.basevolume + j01.servicevolume + j01.addvolume) * 100,2) userate ,row_number() over(order by a01.joindate DESC, a01.email) AS rownum from tbmember a01 inner join tbmemberlicense j01 on a01.email = j01.email left join tbtaxinformation j02 on a01.email = j02.email left join tbmemberusehistory j03 on a01.email = j03.email and(j03.email, j03.useday) in (select email, max(useday) useday from tbmemberusehistory group by email) left join tbcommoncode j04 on j04.groupcode = '100' and j04.commoncode = a01.hostname where 1 = 1 and a01.withdrawalyn != '1'
AND a01.membername like concat(#{searchName},'%')
) as a where 1 = 1
and a.rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
order by a.joindate DESC, a.email

select a.* from(select a01.memberid ,a01.membername -- 회원명 ,a01.email -- 이메일 ,a01.licensegrade -- 라이센스등급 ,a01.celltel -- 회원전화번호 ,a01.businessname -- 사업장명 ,a01.businessnumber -- 사업자등록번호 ,a01.joindate -- 가입일자 ,str_to_date(a01.joindate,'%Y%m%d') strjoindate ,a01.hostname ,j04.codename hostip ,a01.serverdomainname ,case when a01.withdrawalyn = '0' then '회원' when a01.withdrawalyn = '1' then '탈퇴' else '중지' end statusnm -- 회원상태 ,j01.datakeepterm -- 데이터보관기간 ,j01.datakeepunit -- 데이터보관단위(D:일,M:월,Y:년) ,concat(j01.datakeepterm,'(',if(j01.datakeepunit='D','일',if(j01.datakeepunit='M','월', '년')),')') datakeepnm ,j01.basevolume + IFNULL(j01.servicevolume,0) + IFNULL(j01.addvolume,0) totalsoluble -- 전체가용장비 ,j01.basevolume -- 기본장비수량 ,j01.basecharge -- 기본요금(라이센스요금) ,j01.servicevolume -- 서비스장비수량 ,j01.addvolume -- 추가장비수량 ,j01.addcharge -- 추가라이센스요금 ,j01.basecharge + IFNULL(j01.addcharge,0) totcharge -- 라이센스합계금액 ,j02.Representationname -- 대표자 ,j02.businesskind -- 업태 ,j02.businesscondition -- 업종 ,j02.postnumber -- 우편번호 ,j02.address -- 주소 ,j02.detailaddress -- 상세주소 ,concat(j02.postnumber,' ',j02.address,' ',j02.detailaddress) zipaddress ,j03.useyym useyym ,IFNULL(j03.totalvolume,0) totalvolume ,IFNULL(j03.networkvolume,0) networkvolume ,IFNULL(j03.servervolume,0) servervolume ,IFNULL(j03.apvolume,0) apvolume ,IFNULL(j03.dbmsvolume,0) dbmsvolume ,IFNULL(j03.fmsvolume,0) fmsvolume ,IFNULL(j03.totalcharge,0) totalcharge ,concat(substring(j03.useyym,1,4),'.',substring(j03.useyym,5,2)) struseyym ,round(IFNULL(j03.totalvolume,0) / (j01.basevolume + IFNULL(j01.servicevolume,0) + IFNULL(j01.addvolume,0)) * 100,2) userate ,row_number() over(order by j03.useyym DESC) AS rownum from tbmember a01 inner join tbmemberlicense j01 on a01.email = j01.email inner join tbtaxinformation j02 on a01.email = j02.email inner join tbmemberusemonthvv j03 on a01.email = j03.email left join tbcommoncode j04 on j04.groupcode = '100' and j04.commoncode = a01.hostname where 1 = 1 and a01.email = #{searchName} and a01.withdrawalyn != '1') as a where 1 = 1
and a.rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
order by a.useyym DESC

select a.* from( select a01.memberid ,a01.membername -- 회원명 ,a01.email -- 이메일 ,a01.licensegrade -- 라이센스등급 ,a01.celltel -- 회원전화번호 ,a01.businessname -- 사업장명 ,a01.businessnumber -- 사업자등록번호 ,concat(substring(a01.businessnumber,1,3),'-',substring(a01.businessnumber,4,2),'-',substring(a01.businessnumber,6,5)) strbusinessnumber ,a01.joindate -- 가입일자 ,str_to_date(a01.joindate,'%Y%m%d') strjoindate ,a01.hostname ,j04.codename hostip ,a01.serverdomainname ,case when a01.withdrawalyn = '0' then '회원' when a01.withdrawalyn = '1' then '탈퇴' else '중지' end statusnm ,j01.taxhistoryid -- 세금계산서발행내역 ,j01.taxid -- 세금계산서발행기관정보 ,j01.issueyearmonth -- 발행년월 ,j01.issueamount -- 발행금액 ,j01.issuedate -- 발행일자 ,str_to_date(j01.issuedate,'%Y%m%d') strissuedate ,j02.representationname -- 대표자 ,j02.businesskind -- 업태 ,j02.businesscondition -- 업종 ,j02.postnumber -- 우편번호 ,j02.address -- 주소 ,j02.detailaddress -- 상세주소 ,concat(j02.postnumber,' ',j02.address,' ',j02.detailaddress) zipaddress ,j01.createdAt ,row_number() over(order by j01.issueyearmonth DESC, j01.createdAt desc) AS rownum from tbmember a01 inner join tbtaxinformation j02 inner join tbtaxhistory j01 on j02.taxid = j01.taxid on a01.email = j02.email left join tbcommoncode j04 on j04.groupcode = '100' and j04.commoncode = a01.hostname where 1 = 1 and a01.email = #{searchName} and a01.withdrawalyn != '1') as a where 1 = 1
and a.rownum BETWEEN #{firstRecordIndex} AND #{lastRecordIndex}
order by a.issueyearmonth DESC, a.createdAt desc

select 'charge' div, count(useyym) from tbmemberusemonth where email = #{email}  
union all
select 'tax' div, count(useyym) from tbtaxhistory where email = #{email}

mapper.update:
update tbmemberlicense
set addvolume = #{addvolume}
  ,servicevolume = #{servicevolume}
  ,addcharge = #{addcharge}
  ,datakeepterm = #{datakeepterm}
  ,datakeepunit = #{datakeepunit}
where email = #{email}

update tbmember
set serverdomainname = #{serverdomainname}
where email = #{email}

mapper.insert:
insert into tbusedevice(
    usedeviceid     ,userid          ,useday          ,transtatus      ,info            ,
    nmscount        ,smscount        ,dbmscount       ,apcount         ,fmscount        ,
    reason                                                                              )
values(#{usedeviceid},#{userid}      ,date_format(curdate(),'%Y%m%d'),#{transtatus},#{info},
       #{nmscount}  , #{smscount}  ,#{apcount}      ,#{dbmscount}    ,#{fmscount}     ,
       #{reason}                                                                       )
ON DUPLICATE KEY UPDATE
    transtatus  = #{transtatus}
                      ,info        = #{info}
                      ,nmscount    = #{nmscount}
                      ,smscount    = #{smscount}
                      ,dbmscount   = #{dbmscount}
                      ,apcount     = #{apcount}
                      ,fmscount    = #{fmscount}
                      ,reason      = #{reason}
