mapper.select:
select email, membername, grpname, celltel, businessname, businessnumber, licensegrade
from tbmember
where email=#{email}

select companyname, representationname, taxcompanynumber, taxemail, address, detailaddress, businesskind, businesscondition, paid_amount, DATE_FORMAT(updatedAt,'%Y-%m-%d %H:%i:%s') as updatedAt
from tbtaxinformation
where email=#{email}

select DATE_FORMAT(createdAt,'%Y-%m-%d %H:%i:%s') as createdAt
from tbtaxinformation
where email=#{email}

SELECT baselicense
FROM tbcommoncode
WHERE groupcode = '001' AND commoncode = #{commoncode};
